class MaxHeap:
    def __init__(self):
        self.heap = []

    def push(self, val):
        self.heap.append(val)
        index = len(self.heap) - 1
        parent = (index - 1) // 2

        while parent >= 0 and self.heap[parent] < self.heap[index]:
            self.heap[parent], self.heap[index] = self.heap[index], self.heap[parent]
            index = parent
            parent = (index - 1) // 2

    def pop(self):
        if len(self.heap) == 0:
            return None
        if len(self.heap) == 1:
            return self.heap.pop()

        root_val = self.heap[0]
        self.heap[0] = self.heap[-1]
        self.heap.pop()

        index = 0
        left_child = 2 * index + 1
        right_child = 2 * index + 2

        while left_child < len(self.heap):
            if right_child < len(self.heap):
                max_child_index = left_child if self.heap[left_child] > self.heap[right_child] else right_child
            else:
                max_child_index = left_child
            if self.heap[index] < self.heap[max_child_index]:
                self.heap[index], self.heap[max_child_index] = self.heap[max_child_index], self.heap[index]
                index = max_child_index
                left_child = 2 * index + 1
                right_child = 2 * index + 2
            else:
                break

        return root_val

